mapper.select:
SELECT latitude, longitude, COUNT(*) as count
FROM injuryrecord
WHERE admission_date BETWEEN #{startDate} AND #{endDate}

    AND (time_period IN
    
        #{tp}
    
    )

AND latitude IS NOT NULL
AND longitude IS NOT NULL
GROUP BY latitude, longitude

SELECT latitude, longitude, COUNT(*) as count
FROM injuryrecord
WHERE 1=1

    AND YEAR(admission_date) IN
    
        #{y}
    


    AND season IN
    
        #{s}
    


    AND (time_period IN
    
        #{tp}
    
    )

AND latitude IS NOT NULL
AND longitude IS NOT NULL
GROUP BY latitude, longitude

SELECT CASE -- 形如 HH:mm 或 H:mm 或 HH:mm:ss / H:mm:ss，取冒号前的小时 WHEN admission_time REGEXP '^[0-9]{1,2}:[0-9]{1,2}(:[0-9]{1,2})?$' THEN CAST(SUBSTRING_INDEX(admission_time, ':', 1) AS UNSIGNED) -- 形如 HHmm（4位）或 Hmm（3位），去掉最后两位分钟 WHEN admission_time REGEXP '^[0-9]{3,4}$' THEN CAST(SUBSTRING(admission_time, 1, LENGTH(admission_time) - 2) AS UNSIGNED) -- 形如 HHmmss（6位），取前两位小时 WHEN admission_time REGEXP '^[0-9]{6}$' THEN CAST(SUBSTRING(admission_time, 1, 2) AS UNSIGNED) -- 仅小时（1-2位） WHEN admission_time REGEXP '^[0-9]{1,2}$' THEN CAST(admission_time AS UNSIGNED) ELSE NULL END as hour, COUNT(*) as count FROM injuryrecord WHERE 1=1
AND YEAR(admission_date) = #{year}
AND season IN
#{s}
AND admission_date >= #{startDate}
AND admission_date <= #{endDate}
AND admission_time IS NOT NULL AND admission_time != '' GROUP BY hour HAVING hour IS NOT NULL AND hour BETWEEN 0 AND 23 ORDER BY hour
