mapper.select:
SELECT time_period, month, patient_count FROM ( -- 原始时间段数据 SELECT CASE WHEN ir.time_period = 0 THEN 'night_0_7' WHEN ir.time_period = 1 THEN 'morning_rush_8_9' WHEN ir.time_period = 2 THEN 'lunch_rush_10_11' WHEN ir.time_period = 3 THEN 'afternoon_12_16' WHEN ir.time_period = 4 THEN 'evening_rush_17_19' WHEN ir.time_period = 5 THEN 'night_20_23' ELSE 'unknown' END as time_period, MONTH(ir.admission_date) as month, COUNT(*) as patient_count FROM InjuryRecord ir WHERE 1=1
AND YEAR(ir.admission_date) = #{year}
AND ir.admission_date BETWEEN #{startDate} AND #{endDate}
AND ir.season = #{season}
AND ir.time_period = #{timePeriod}
AND ir.time_period IS NOT NULL GROUP BY CASE WHEN ir.time_period = 0 THEN 'night_0_7' WHEN ir.time_period = 1 THEN 'morning_rush_8_9' WHEN ir.time_period = 2 THEN 'lunch_rush_10_11' WHEN ir.time_period = 3 THEN 'afternoon_12_16' WHEN ir.time_period = 4 THEN 'evening_rush_17_19' WHEN ir.time_period = 5 THEN 'night_20_23' ELSE 'unknown' END, MONTH(ir.admission_date) ) t ORDER BY CASE time_period WHEN 'night_0_7' THEN 1 WHEN 'morning_rush_8_9' THEN 2 WHEN 'lunch_rush_10_11' THEN 3 WHEN 'afternoon_12_16' THEN 4 WHEN 'evening_rush_17_19' THEN 5 WHEN 'night_20_23' THEN 6 WHEN 'total' THEN 7 ELSE 8 END, month

SELECT ir.injury_location as injury_site, COUNT(*) as patient_count, ROUND(COUNT(*) * 100.0 / (SELECT COUNT(*) FROM InjuryRecord WHERE admission_date BETWEEN #{startDate} AND #{endDate}), 2) as percentage FROM InjuryRecord ir WHERE ir.admission_date BETWEEN #{startDate} AND #{endDate} AND ir.injury_location IS NOT NULL AND ir.injury_location != ''
AND ir.season = #{season}
AND ir.time_period = #{timePeriod}
GROUP BY ir.injury_location ORDER BY COUNT(*) DESC LIMIT 10

SELECT 
    MONTH(ir.admission_date) as month,
    ir.injury_cause_category,
    COUNT(*) as patient_count
FROM InjuryRecord ir
WHERE 1=1

    AND YEAR(ir.admission_date) = #{year}


    AND ir.admission_date BETWEEN #{startDate} AND #{endDate}


    AND ir.season = #{season}


    AND ir.time_period = #{timePeriod}

AND ir.injury_cause_category IS NOT NULL
GROUP BY MONTH(ir.admission_date), ir.injury_cause_category
ORDER BY MONTH(ir.admission_date), ir.injury_cause_category

SELECT CASE WHEN iss.iss_score IS NULL OR iss.iss_score = 0 THEN 'light' WHEN iss.iss_score BETWEEN 1 AND 8 THEN 'light' WHEN iss.iss_score BETWEEN 9 AND 15 THEN 'moderate' WHEN iss.iss_score >= 16 THEN 'severe' ELSE 'light' END as severity_level, COUNT(*) as patient_count FROM InjuryRecord ir INNER JOIN iss_patient_injury_severity iss ON ir.patient_id = iss.patient_id WHERE 1=1
AND ir.admission_date BETWEEN #{startDate} AND #{endDate}
AND YEAR(ir.admission_date) = #{year}
AND ir.season = #{season}
AND ir.time_period = #{timePeriod}
GROUP BY CASE WHEN iss.iss_score IS NULL OR iss.iss_score = 0 THEN 'light' WHEN iss.iss_score BETWEEN 1 AND 8 THEN 'light' WHEN iss.iss_score BETWEEN 9 AND 15 THEN 'moderate' WHEN iss.iss_score >= 16 THEN 'severe' ELSE 'light' END ORDER BY CASE WHEN iss.iss_score IS NULL OR iss.iss_score = 0 THEN 'light' WHEN iss.iss_score BETWEEN 1 AND 8 THEN 'light' WHEN iss.iss_score BETWEEN 9 AND 15 THEN 'moderate' WHEN iss.iss_score >= 16 THEN 'severe' ELSE 'light' END
